double cartToPolar = y2 <= x2 ? x*y/(x2 + 0.28f*y2 + DBL_EPSILON)  + tmp :
tmp1 - x*y/(y2 + 0.28f*x2 + DBL_EPSILON);
#ifdef DEGREE
cartToPolar *= (180/CV_PI);
#endif
dst1[dst1_index] = magnitude;
dst2[dst2_index] = cartToPolar;
}
}
#endif

