__kernel __attribute__((reqd_work_group_size(BLK_X,BLK_Y,1))) void sep_filter_singlepass
(
__global uchar* Src,
const uint      SrcPitch,
const int       srcOffsetX,
const int       srcOffsetY,
__global uchar* Dst,
const int       DstOffset,
const uint      DstPitch,
