&b1, &b2);
xBase+=xsize;
if(xBase<c_winSize_x)
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[2], &dIdx_patch[2], &dIdy_patch[2],
&b1, &b2);
}
yBase+=ysize;
{
xBase=xid;
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[3], &dIdx_patch[3], &dIdy_patch[3],
&b1, &b2);
xBase+=xsize;
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[4], &dIdx_patch[4], &dIdy_patch[4],
&b1, &b2);
xBase+=xsize;
if(xBase<c_winSize_x)
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[5], &dIdx_patch[5], &dIdy_patch[5],
&b1, &b2);
}
yBase+=ysize;
if(yBase<c_winSize_y)
{
xBase=xid;
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[6], &dIdx_patch[6], &dIdy_patch[6],
&b1, &b2);
xBase+=xsize;
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[7], &dIdx_patch[7], &dIdy_patch[7],
&b1, &b2);
xBase+=xsize;
if(xBase<c_winSize_x)
GetPatch4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_add, &Dx_add, &Dy_add,
&b1, &b2);
}
reduce2(b1, b2, smem1, smem2, tid);
b1 = smem1[0];
b2 = smem2[0];
barrier(CLK_LOCAL_MEM_FENCE);
float2 delta;
delta.x = A12 * b2 - A22 * b1;
delta.y = A12 * b1 - A11 * b2;
nextPt +=delta;
if (fabs(delta.x) < THRESHOLD && fabs(delta.y) < THRESHOLD)
break;
}
D = 0.0f;
if (calcErr)
{
yBase=yid;
{
xBase=xid;
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[0], &D);
xBase+=xsize;
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[1], &D);
xBase+=xsize;
if(xBase<c_winSize_x)
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[2], &D);
}
yBase+=ysize;
{
xBase=xid;
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[3], &D);
xBase+=xsize;
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[4], &D);
xBase+=xsize;
if(xBase<c_winSize_x)
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[5], &D);
}
yBase+=ysize;
if(yBase<c_winSize_y)
{
xBase=xid;
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[6], &D);
xBase+=xsize;
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_patch[7], &D);
xBase+=xsize;
if(xBase<c_winSize_x)
GetError4(J, nextPt.x + xBase + 0.5f, nextPt.y + yBase + 0.5f,
&I_add, &D);
}
reduce1(D, smem1, tid);
}
if (tid == 0)
{
nextPt += c_halfWin;
nextPts[gid] = nextPt;
if (calcErr)
err[gid] = smem1[0] / (float)(3 * c_winSize_x * c_winSize_y);
