#define PROCESS(y, x) \
temp = LDS_DAT[mad24(l_y + y, width, l_x + x)]; \
res = MORPH_OP(res, temp);
#define ELEM(i, l_edge, r_edge, elem1, elem2) (i) < (l_edge) | (i) >= (r_edge) ? (elem1) : (elem2)
#if defined OP_GRADIENT || defined OP_TOPHAT || defined OP_BLACKHAT
