#   define DIST_RES(x) sqrt(x)
#elif (DIST_TYPE == 6)
#   if (8 == kercn)
typedef int8 value_type;
#   elif (4 == kercn)
typedef int4 value_type;
#   else
typedef int value_type;
#   endif
typedef int result_type;
#   define DIST(x, y) result += popcount( (x) ^ (y) )
#   define DIST_RES(x) (x)
